import React from 'react';
import './sidebar.css';


export const Sidebar = ({ width, height, children }) => {
    const [xPosition, setX] = React.useState(-width);
  
    const toggleMenu = () => {
      if (xPosition < 10) {
        setX(300);
      } else {
        setX(50);
      }
    };
  
    React.useEffect(() => {
      setX(0);
    }, []);
    return (
      <React.Fragment>
        <div
          className="side-bar"
          style={{
            transform: `translatex(${xPosition}px)`,
            width: width,
            minHeight: height
          }}
        >
        <div className="profile-pic">
          <h1>picture goes here</h1>
        </div>

        <div className="contents">
          <h1>INSERT UNTOGGLED IMAGES</h1>
        </div>
          <button
            onClick={() => toggleMenu()}
            className="toggle-menu"
            style={{
              transform: `translate(${width}px, 30vh)`
            }}
          ></button>
          <div className="content">{children}
          <h1>LEARNING OBJECTIVES</h1>
          <h1>VIDEO TUTORIAL</h1>
          <h1>PREVIEW PROJECT</h1>
          <h1>CHECK SUBMISSIONS</h1>
          <h1>OFFLINE ACTIVITIES</h1>
          <h1>VIEW QUIZ RESULTS</h1>
        </div>
        </div>
      </React.Fragment>
    );
  };

  export default Sidebar;
